@bot.message_handler(commands=['start'])
def welcome(message):
 
    # keyboard
    markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
    item1 = types.KeyboardButton("üé≤ –ü—Ä–æ–¥—É–∫—Ç")
 
    markup.add(item1)
 
    bot.send_message(message.chat.id, "–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å, {0.first_name}!\n–Ø - <b>{1.first_name}</b>, –±–æ—Ç, –∫–æ—Ç–æ—Ä—ã–π –ø–æ–º–æ–∂–µ—Ç —Ç–µ–±–µ —Ä–∞–∑–æ–±—Ä–∞—Ç—å—Å—è —Å —Ä–∞–±–æ—Ç–æ–π..".format(message.from_user, bot.get_me()),
        parse_mode='html', reply_markup=markup)
 
@bot.message_handler(content_types=['text'])
def lalala(message):
    if message.chat.type == 'private':
        if message.text == 'üé≤ –ü—Ä–æ–¥—É–∫—Ç':
            '''
            bot.send_message(message.chat.id, main_text_uslugi)
            bot.send_photo(message.chat.id, "https://i.imgur.com/aligt5u.jpg")
            '''
            
            markup = types.InlineKeyboardMarkup(row_width=1)
            item1 = types.InlineKeyboardButton("–î–æ–º–∞—à–Ω–∏–π –∏–Ω—Ç–µ—Ä–Ω–µ—Ç", callback_data='internet')
            item2 = types.InlineKeyboardButton("WINK", callback_data='wink')
            item3 = types.InlineKeyboardButton("–ú–æ–±–∏–ª—å–Ω–∞—è —Å–≤—è–∑—å", callback_data='mvno')
            item4 = types.InlineKeyboardButton("–í–∏–¥–µ–æ–Ω–∞–±–ª—é–¥–µ–Ω–∏–µ", callback_data='video')
            item5 = types.InlineKeyboardButton("–ú–∞—Ä—É—Å—è", callback_data='marusya')
            item6 = types.InlineKeyboardButton("–ì–∞—Ä–∞–Ω—Ç–∏—è", callback_data='garant')
            item7 = types.InlineKeyboardButton("–¢–∞—Ä–∏—Ñ—ã", callback_data='tarif')
            item8 = types.InlineKeyboardButton("–¢–µ—Ö–Ω–∏—á–µ—Å–∫–∏–µ —Ä–∞–±–æ—Ç—ã", callback_data='tech')
            
 
            markup.add(item1, item2, item3, item4, item5, item6, item7, item8)
            
            bot.send_message(message.chat.id, "–ß—Ç–æ –Ω—É–∂–Ω–æ?", reply_markup=markup)
 
        else:
            bot.send_message(message.chat.id, '–Ø –Ω–µ –∑–Ω–∞—é, —á—Ç–æ –æ—Ç–≤–µ—Ç–∏—Ç—å üò¢')
 
